# 非递归写法
class Solution:
    def isUgly(self, num: int) -> bool:
        if num <= 0:
            return False
        for i in (2, 3, 5):
            while num % i == 0:
                num /= i
        return num == 1

while(num % 2 === 0)   num = num / 2;
while(num % 3 === 0)   num = num / 3;
while(num % 5 === 0)   num = num / 5;

return num === 1;

