class Solution:
   def reverseBetween(self, head: ListNode, m: int, n: int) -> ListNode:
       pre = None
       cur = head
       i = 0
       p1 = p2 = p3 = p4 = None
       dummy = ListNode(0)
       dummy.next = head

       # ...

       if p1:
           p1.next = p3
       else:
           dummy.next = p3
       if p2:
           p2.next = p4

       return dummy.next

while cur:
       i += 1
       if i == m - 1:
           p1 = cur
       next = cur.next
       if m < i <= n:
           cur.next = pre

       if i == m:
           p2 = cur
           p2.next = None

       if i == n:
           p3 = cur

       if i == n + 1:
           p4 = cur

       pre = cur
       cur = next
